def read_json_file(file_path):
    """
    Lê um arquivo JSON do caminho especificado e retorna os dados como um dicionário.
    
    :param file_path: O caminho do arquivo JSON.
    :return: Dados do arquivo JSON como um dicionário.
    """
    try:
        with open(file_path, 'r') as file:
            data = json.load(file)
        return data
    except FileNotFoundError:
        print(f"Arquivo não encontrado: {file_path}")
        return None
    except json.JSONDecodeError:
        print(f"Erro ao decodificar o arquivo JSON: {file_path}")
        return None
    except Exception as e:
        print(f"Ocorreu um erro ao ler o arquivo JSON: {e}")
        return None